import { Col } from "react-bootstrap";
import { Route, Routes, Router } from 'react-router-dom';
import { Link } from "react-router-dom";
import Contact from "./Contact";
import img from "../assets/img/1.jpg";
import { useState, useEffect } from "react";

export const ProjectCard = ({ index, title, description }) => {
  const [indexy, setItems] = useState([]);
  useEffect(() => {
    localStorage.setItem('indexy', JSON.stringify({index}));
  }, [indexy]);
  return (
    <Col size={12} sm={6} md={4}>
      <div className="proj-imgbx">
        <img src={img} />
        <div className="proj-txtx">
          <h4>{index}</h4>
          <span>{description}</span><br></br>
          <div className="link">
            <button onClick={useEffect}>
              <Link to={{
                pathname: "/projects", state: [{ id: 19923 }]
              }}><span> Details</span></Link>
            </button>
          </div>
        </div>
      </div>
    </Col>
  )
}
